import { QuestionNavbar } from "../QuestionNavbar.js";
import { Info } from "./components/Info.js";

export const Info37 = () => {
  return (
    <div className="infos">
      <QuestionNavbar />
      <Info
        title={[
          "Häirintä ja uhkailu voivat aiheuttaa pelkoa ja itsesensuuria sekä haitata moniäänisyyden toteutumista. Kritiikki on hyväksyttävää ja usein toivottavaa - henkilökohtaiseen loukkaamiseen pyrkivä häirintä ei. Suomen rikoslaki suojaa kunnianloukkaukselta, uhkaukselta ja kiihottamiselta kansanryhmiä kohtaan kaikkia kansalaisia.​",
          "Haittaako pelko häirinnän kohteeksi joutumisesta moniäänisyyden toteutumista työssäsi? Oletko kokenut häirintää ja miten suhtaudut siihen? Saatko tarvittaessa tukea ja apua tilanteissa? Millaista on reilu kritiikki?​​",
        ]}
        options={[
          {
            value:
              "Neuvoja ja ohjeita häirintätilanteisiin: Ylen turvallisemman vuorovaikutuksen ohje​​",
            navigate: "https://www.suomenpen.fi/en/vihapuhetyokalupakki/",
          },
          {
            value:
              "Ilmari Hiltusen väitöstutkimus toimittajiin kohdistuvasta häirinnästä: External Interference in Finnish Professional Journalism​",
            navigate: "https://trepo.tuni.fi/handle/10024/141075",
          },
          {
            value:
              "Suomenkielinen haastattelu samasta tutkimuksesta: Ilmari Hiltunen: Vihamieliset yleisöt tuovat uudenlaisia uhkia suomalaisten journalistien työhön | Tampereen korkeakouluyhteisö​",
            navigate: "https://www.tuni.fi/fi/ajankohtaista/ilmari-hiltunen-vihamieliset-yleisot-tuovat-uudenlaisia-uhkia-suomalaisten",
          },
          {
            value:
              "Verkkovihan tuottamista ja levittämistä koskeva tutkimus: Verkkoviha : Vihapuheen tuottajien ja levittäjien verkostot, toimintamuodot ja motiivit​​",
            navigate: "https://julkaisut.valtioneuvosto.fi/handle/10024/164244",
          },
          {
            value:
              "Vihapuheen merkityksestä yhteiskunnalliselle päätöksenteolle: Viha vallassa : Vihapuheen vaikutukset yhteiskunnalliseen päätöksentekoon​",
            navigate: "https://julkaisut.valtioneuvosto.fi/handle/10024/161812",
          },
        ]}
      />
    </div>
  );
};
