import { useState, useEffect } from "react";

import { Container, Nav, Navbar } from "react-bootstrap";

import logo from "../assets/logo.png";

export const NavBar = () => {
  const [activeLink, setActiveLink] = useState("home");
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, []);

  const onUpdateActiveLink = (value) => {
    setActiveLink(value);
  };

  return (
    <Navbar expand="lg" className={scrolled ? "scrolled" : ""}>
      <Container>
        <Navbar.Brand
          href="#home"
          className={
            activeLink === "home" ? "active-navbar-link" : "navbar-link"
          }
          onClick={() => onUpdateActiveLink("home")}
        >
          <img src={logo} alt="Logo" />
        </Navbar.Brand>


        <Navbar.Collapse className="justify-content-end">
          <Nav>
            <Nav.Link
              href="#content"
              className={
                activeLink === "content" ? "active-navbar-link" : "navbar-link"
              }
              onClick={() => onUpdateActiveLink("content")}
            >
              Content
            </Nav.Link>
            <Nav.Link
              href="#aboutus"
              className={
                activeLink === "aboutus" ? "active-navbar-link" : "navbar-link"
              }
              onClick={() => onUpdateActiveLink("aboutus")}
            >
              About us
            </Nav.Link>
            <Nav.Link
              href="#partners"
              className={
                activeLink === "partners" ? "active-navbar-link" : "navbar-link"
              }
              onClick={() => onUpdateActiveLink("partners")}
            >
              Our partners
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};
