import { QuestionNavbar } from "../QuestionNavbar.js";
import { Info } from "./components/Info.js";

export const Info23 = () => {
  return (
    <div className="infos">
      <QuestionNavbar />
      <Info
        title={[
          "Reilu mediajulkisuus on dialoginen ja erilaisia ihmisiä näkemyksineen kunnioittava. Uudet näkökulmat tai uudenlainen käsittelytapa saavat usein aikaan keskustelua ja vuorovaikutusta yleisön kanssa. Reilun keskustelun ei tarvitse merkitä samanmielisyyttä. Monesti reilu debatti on yksiäänisyyttä kiinnostavampaa ja kehittävämpää.​",
          "Häirintä ja uhkailu voivat aiheuttaa pelkoa ja itsesensuuria sekä haitata moniäänisyyden toteutumista. Kritiikki on hyväksyttävää ja usein toivottavaa - henkilökohtaiseen loukkaamiseen pyrkivä häirintä ei. Suomen rikoslaki suojaa kunnianloukkaukselta, uhkaukselta ja kiihottamiselta kansanryhmiä kohtaan kaikkia kansalaisia.​",
          "Haittaako pelko häirinnän kohteeksi joutumisesta moniäänisyyden toteutumista työssäsi? Oletko kokenut häirintää ja miten suhtaudut siihen? Saatko tarvittaessa tukea ja apua tilanteissa? Millaista on reilu kritiikki?​​",
        ]}
        options={[
          {
            value:
              "Pöyhtäri, R., Väliverronen, J., & Ahva, L. (2016): Suomalaisen journalistin itseymmärrys muutosten keskellä. Media & Viestintä, 39(1)​",
            navigate: "https://doi.org/10.23983/mv.61434​",
          },
          {
            value:
              "Heikkilä, Hellman & Ovaska 2023: Mä lehden luin: Yleisön suhde painettuun ja digitaaliseen sanomalehteen",
            navigate: "https://trepo.tuni.fi/handle/10024/147335",
          },
          {
            value:
              "Ylen vuorovaikutuspäällikön Sami Koiviston puheenvuoro: Sami Koivisto: Jutut ovat parempia, jos toimittaja mieltää ne osaksi dialogia yleisön kanssa",
            navigate: "https://yle.fi/aihe/artikkeli/2018/10/24/sami-koivisto-jutut-ovat-parempia-jos-toimittaja-mieltaa-ne-osaksi-dialogia",
          },
          {
            value:
              "Neuvoja ja ohjeita häirintätilanteisiin: Ylen turvallisemman vuorovaikutuksen ohje​",
            navigate: "https://www.decatutkimus.fi/",
          },
        ]}
      />
    </div>
  );
};
