import { QuestionNavbar } from "../QuestionNavbar.js";
import { Info } from "./components/Info.js";

export const Info24 = () => {
  return (
    <div className="infos">
      <QuestionNavbar />
      <Info
        title={[
          "Moniäänisyys on noussut tärkeäksi media-alan keskusteluissa. Moniäänisyyttä koskevat valinnat vaikuttavat journalistisen prosessin kaikissa vaiheissa.​​​​",
          "Vaikka moniäänisyyden tarpeellisuudesta vallitsee journalismissa yhteisymmärrys, sen soveltamisesta käytäntöön ollaan usein erimielisiä: Kenen ääntä pitäisi vahvistaa? Kenen ääni puuttuu? Voisiko kansalaisten ääntä saada paremmin esiin? Voiko moniäänisyyttä olla liikaa?​​",
          "Moniäänisyyttä pohdittaessa on hyödyllistä kysyä omaa suhdetta moniäänisyyteen: Onko tavoitteesi kuvata monimuotoista maailmaa totuudenmukaisemmin tai parantaa maailmaa nostamalla esiin aiheita tai näkökulmia?​​",
          "Moniäänisyys on asia, jota jokainen toimittaja voi omassa työskentelyssään ottaa huomioon, mutta kuten Matleena Ylikoski Moniäänisyysmittarinsa loppuraportissa suosittaa olisi hyvä rekrytoida toimituksesta ryhmä, joka keskittyy toimituksen moniäänisyyteen. Ylikosken raportissa on erilaisia ehdotuksia medioiden moniäänisyysprojekteilla.​",
        ]}
        options={[
          {
            value: "Moniäänisyydestä ja sen mittaamisesta​",
            navigate: "https://trepo.tuni.fi/handle/10024/146461",
          },
          {
            value: "Kenen Tietoo? -Opas moninaisempaan ja inklusiivisempaan journalismiin",
            navigate: "https://trepo.tuni.fi/handle/10024/149682",
          },
        ]}
      />
    </div>
  );
};
