import React, { useEffect } from "react";

import { Row, Col, Card } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { ArrowRight } from "react-bootstrap-icons";

import AOS from "aos";
import "aos/dist/aos.css";

export const Info = ({ title, options, next }) => {
  const navigate = useNavigate();

  useEffect(() => {
    AOS.init();
  }, []);

  return (
    <section className="infobanner" id="info">
      <center>
        <Card className="infocard" id="infocard">
          {title.map((text) => {
            return (
              <Card.Title
                id="infotitle"
                data-aos="fade-down"
                data-aos-duration="2000"
              >
                {text}
              </Card.Title>
            );
          })}
          {options.map((choice) => {
            return (
              <Row id="inforow" data-aos="fade-left" data-aos-duration="2000">
                <Col xs={4} md={2} xl={2}>
                  <button
                    onClick={(e) => {
                      e.preventDefault();
                      window.location.href = `${choice.navigate}`;
                    }}
                  >
                    Read more
                  </button>
                </Col>
                <Col>
                  <Card.Text id="infocontent">{choice.value}</Card.Text>
                </Col>
              </Row>
            );
          })}
          {next ? (
            <Row data-aos="fade-right" data-aos-duration="2000">
              <Col md={{ span: 10, offset: 10 }}>
                <button id="nextBtn" onClick={() => navigate(`/${next}`)}>
                  Next <ArrowRight size={25} />
                </button>
              </Col>
            </Row>
          ) : (
            <></>
          )}
        </Card>
      </center>
    </section>
  );
};
