import { QuestionNavbar } from "../QuestionNavbar.js";
import { Info } from "./components/Info.js";

export const Info35 = () => {
  return (
    <div className="infos">
      <QuestionNavbar />
      <Info
        title={[
          "Erilaisilla teksteillä ja juttutyypeillä on erilaisia tehtäviä. Kontekstoinnilla eli jutun asettamisella sopivaan asiayhteyteen voidaan rakentaa yhteistä ymmärrystä asiasta, jolloin väärinymmärrysten vaara vähenee.​",
          "Missä asiayhteydessä juttu julkaistaan? Kertovatko jutun eri elementit samasta asiasta? Kenelle juttu on suunniteltu? Onko yleisön tulkinnallinen konteksti tiedossa? Onko jutun konteksti kuvattu selkeästi ilman väärinymmärryksen vaaraa?​​",
        ]}
        options={[
          {
            value:
              "Luettavaa: Kangaspunta, V. (2021) Verkkouutisten kommentit: Julkista osallistumista ja henkilökohtaisten päämäärien tavoittelua. Media & Viestintä 44 (1): 210–214 ​​",
            navigate: "https://journal.fi/mediaviestinta/article/download/107309/62759",
          },
          {
            value:
              "Sovittelujournalismin käsikirjan luku Kerronta: Kerronta (sovittelujournalismi.fi)​",
            navigate: "https://sovittelujournalismi.fi/kasikirja/kerronta/",
          },
        ]}
      />
    </div>
  );
};
