import { QuestionNavbar } from "../QuestionNavbar.js";
import { Info } from "./components/Info.js";

export const Info23 = () => {
  return (
    <div className="infos">
      <QuestionNavbar />
      <Info
        title={[
          "Journalistin velvollisuus on pyrkiä totuudenmukaiseen tiedonvälitykseen, ja tiedot on tarkistettava mahdollisimman hyvin – myös silloin kun ne on aikaisemmin julkaistu. Tietolähteisiin on suhtauduttava kriittisesti. Erityisen tärkeää se on kiistanalaisissa asioissa (JSN 2014).​",
          "Totuuden löytäminen voi kuitenkin jäädä vajaaksi, vaikka yksittäisten tietojen paikkansapitävyyden varmistamisessa onnistuttaisiinkin (Kuutti 2015). Tämä vaatii tasapainoilua tietojen varmistamisen ja nopean julkaisemisen synnyttämien ristiriitaisten paineiden keskellä.​​",
          "Erityisesti tietyt aiheet, kuten koronapandemian hallinta tai ilmastotoimet, ovat tehneet näkyviksi millaisia kiistoja faktoina esitettäviin tietoihin voi liittyä ja miten ne voivat heikentää joidenkin kansalaisten luottamusta journalistiseen mediaan. Tutkimushankkeemme haastatteluaineiston mukaan valtavirtamedian tarjoamat faktat pitäisi esittää läpinäkyvästi ja seurattavasti.​",
          "Suhtaudutko kriittisesti kaikkiin tietolähteisiin? Voitko todentaa myös auktorisoitujen ja virallisten tietolähteiden tarjoamia tietoja? Otatko huomioon tietolähteiden taloudelliset ja poliittiset pyrkimykset? Voisivatko vaihtoehtoiset tutkimustulokset olla mukana tietojen arvioimisessa?​​",
        ]}
        options={[
          {
            value:
              "Sankari, V., & Wiberg, M. (2019): Tarkistuksissa vielä parannettavaa: Ylen faktantarkistuksen tarkistus . Media & Viestintä, 42(4)​",
            navigate: "https://raremedia.fi/kenentietoo/",
          },
          {
            value:
              "Kuutti (toim.) 2015: Todenmukainen journalismi. Jyväskylän yliopisto.",
            navigate: "https://jyx.jyu.fi/bitstream/handle/123456789/46081/978-951-39-6101-5.pdf",
          },
          {
            value:
              "Meneillään oleva DECA-hanke tutkii yhteiskunnan tiedollisia järjestelmiä, käytänteitä ja ongelmia useista näkökulmista, tutustu",
            navigate: "https://www.decatutkimus.fi/",
          },
        ]}
      />
    </div>
  );
};
