import React, { useEffect } from "react";

import { Row, Col, Card } from "react-bootstrap";
import { useNavigate } from 'react-router-dom';

import AOS from "aos";
import "aos/dist/aos.css";

export const Question = ({ title, options }) => {
  useEffect(() => {
    AOS.init();
  }, []);

  const navigate = useNavigate();

  return (
    <section className="questionbanner" id="question">
      <center>
        <Card className="questioncard" id="questioncard">
          <Card.Title
            id="questiontitle"
            data-aos="fade-down"
            data-aos-duration="2000"
          >
            {title}
          </Card.Title>
          {options.map((choice) => {
            return (
              <Row id="optionrow" data-aos="fade-left" data-aos-duration="2000">
                <Col xs={2} md={1} xl={1}>
                  <button onClick={() => navigate(`/${choice.navigate}`)}>Select</button>
                </Col>
                <Col>
                  <Card.Text id="optioncontent">{choice.value}</Card.Text>
                </Col>
              </Row>
            );
          })}
        </Card>
      </center>
    </section>
  );
};
