import { QuestionNavbar } from "../QuestionNavbar.js";
import { Info } from "./components/Info.js";

export const Info32 = () => {
  return (
    <div className="infos">
      <QuestionNavbar />
      <Info
        title={[
          "Kansalaisten näkemysten kuuluminen mediajulkisuudessa on olennaista demokraattisen kansalaisyhteiskunnan toimivuuden näkökulmasta. Muuttunut mediaympäristö tuo haasteita julkiselle keskustelulle ja kansalaisten viestinnällisille oikeuksille (Reunanen & Herkman 2020). Yleisöt ovat eriytyneet, ja heikentynyt luottamus mediaan, ja erityisesti uutismediaan on ajanut kansalaisia vaihtoehtoisten tietolähteiden pariin. Luottamuksen puute liittyy usein kokemukseen siitä, etteivät erilaiset näkemykset ja tiedonlähteet tule mediajulkisuudessa edustetuiksi (Ojala & Matikainen 2022; Mäkinen &Pienimäki 2023).​",
          "Miten kansalaisten erilaiset näkemykset esiintyvät jutussasi? Voitko osallistaa yleisöä juttuprosessin eri vaiheisiin? Miten yleisön kontribuutio voisi olla hyödyksi työssäsi?​​",
        ]}
        options={[
          {
            value:
              "Mäkinen, M. (2021) Reilut mediasisällöt kansalaisyhteiskuntaa yhdistämässä: Kansalaisyhteiskunta.​​",
            navigate: "https://kansalaisyhteiskunta.fi/reilut-mediasisallot-kansalaisyhteiskuntaa-yhdistamassa/",
          },
          {
            value:
              "Yleisradion Hyvin sanottu: hanke​",
            navigate: "https://yle.fi/aihe/hyvin-sanottu",
          },
          {
            value:
              "Mäkinen, M. & Pienimäki, M. (tulossa) Inklusiivisen mediajulkisuuden haasteet ja mahdollisuudet.​",
            navigate: "",
          },
        ]}
      />
    </div>
  );
};
