import { QuestionNavbar } from "../QuestionNavbar.js";
import { Info } from "./components/Info.js";

export const Info10 = () => {
  return (
    <div className="infos">
      <QuestionNavbar />
      <Info
        title={[
          "Hienoa. Voisitko haastatella henkilöitä, joihin asia vaikuttaa? Miten saisit mukaan asianosaisten näkemykset ja kokemukset niitä kunnioittaen? Miten tavoittaa nämä henkilöt?​​",
          "Yhdistysten ja järjestöjen kautta voi löytää asiaan perehtyneitä henkilöitä ja haastateltavia. Kolmannen sektorin hankkeet voivat olla hyvä linkki aiheeseen ja asianosaisiin.  Sosiaalinen media voi olla apuna aiheen kartoittamisessa ja kokemusten kuuntelemisessa. Avoimella asenteella verkostoituminen avartaa omiakin näkökulmia.​​​",
        ]}
        options={[
          {
            value: "Opas moninaisempaan ja inklusiivisempaan journalismiin​",
            navigate: "https://raremedia.fi/kenentietoo/",
          },
          {
            value: "Miten aikuisten perustaidot määritellään?",
            navigate: "https://journal.fi/aikuiskasvatus/article/view/88375",
          },
          {
            value:
              "Sovittelujournalismin käsikirjan luku Kohtaaminen ja Kriittisyys",
            navigate: "https://sovittelujournalismi.fi/kasikirja/kriittisyys/",
          },
          {
            value:
              "Sopiva ry:n ja Diakonissalaitoksen tuottama opas romanisiirtolaisten journalistisesta kohtelusta voi tarjota toimivia menetelmiä ja auttaa tunnistamaan riskejä myös muiden vähemmistöjen käsittelyssä",
            navigate:
              "https://www.hdl.fi/ita-euroopan-romanit-opas-journalisteille/",
          },
        ]}
        next={"study/11"}
      />
    </div>
  );
};
