import { QuestionNavbar } from "../QuestionNavbar.js";
import { Info } from "./components/Info.js";

export const Info22 = () => {
  return (
    <div className="infos">
      <QuestionNavbar />
      <Info
        title={[
          "Viranomaisten auktorisoimat tutkimuslaitokset ja instituutiot ovat monesti ensimmäinen ja selkein tarttumakohta lähteisiin. Usein media suosii vakiintuneita asiantuntijalähteitä ja viranomaislausuntoja.​",
          "Eri alojen tutkimustietokannat ja e-aineistot tarjoavat kotimaisia ja kansainvälisiä tutkimuslähteitä. Yliopistojen ja tutkimuslaitosten tutkimusportaalien kautta löydät tutkijoita, julkaisuja, projekteja ja tutkimusyksiköitä.​",
          "Yhdistykset ja eri alojen järjestöt voivat olla arvokas apu näkökulmien monipuolistamisessa ja sopivien haastateltavien löytämisessä. Yhdistysten ja järjestöjen kautta voi löytää asiaan perehtyneitä henkilöitä ja haastateltavia. Kolmannen sektorin hankkeet voivat olla hyvä linkki aiheeseen ja asianosaisiin. Sosiaalinen media voi olla apuna aiheen kartoittamisessa ja kokemusten kuuntelemisessa.​​",
          "Avoimella asenteella verkostoituminen avartaa omiakin näkökulmia.​​",
        ]}
        options={[
          {
            value:
              "Reunanen, E. (2022) Poliittinen polarisaatio ja journalismi ja Kunelius, Noppari, Reunanen (2010) Media vallan verkoissa​​",
            navigate:
              "https://trepo.tuni.fi/handle/10024/144761",
          },
          {
            value: "Lassila-Merisalo, M. (25.4.2022): Yksilöiden selviytymiskertomukset ovat arveluttavaa journalismia.",
            navigate: "https://journalisti.fi/nakokulmat/2022/04/yksiloiden-selviytymiskertomukset-ovat-arveluttavaa-journalismia-kirjoittaa-dosentti-maria-lassila-merisalo/",
          },
          {
            value: "Tiedetoimittaja Jani Kaaro (2022): kommentoi dosentti Maria Lassila-Merisalon ja lehtori Lauri Haapasen pitämää työpajaa Journalismin etiikka -seminaarissa 25.-26.8.2022. Journalismi tarinoi ja siteeraa – kuinka käy todenmukaisuuden?",
            navigate: "https://www.tiedetoimittajat.fi/journalismi-tarinoi-ja-siteeraa-kuinka-kay-todenmukaisuuden/",
          },
        ]}
      />
    </div>
  );
};
