import { QuestionNavbar } from "../QuestionNavbar.js";
import { Info } from "./components/Info.js";

export const Info5 = () => {
  return (
    <div className="infos">
      <QuestionNavbar />
      <Info
        title={[
          "Moniäänisyys on noussut tärkeäksi media-alan keskusteluissa. Moniäänisyyttä koskevat valinnat vaikuttavat journalistisen prosessin kaikissa vaiheissa. Vaikka moniäänisyyden tarpeellisuudesta vallitsee journalismissa yhteisymmärrys, sen soveltamisesta käytäntöön ollaan usein erimielisiä: Kenen ääntä pitäisi vahvistaa? Kenen ääni puuttuu? Voisiko kansalaisten ääntä saada paremmin esiin? Voiko moniäänisyyttä olla liikaa?",
          "Moniäänisyyttä pohdittaessa on hyödyllistä kysyä omaa suhdetta moniäänisyyteen: Onko tavoitteesi kuvata monimuotoista maailmaa totuudenmukaisemmin tai parantaa maailmaa nostamalla esiin aiheita tai näkökulmia?​",
        ]}
        options={[
          {
            value:
              "Moniäänisyydestä ja sen mittaamisesta​​​​",
            navigate: "https://trepo.tuni.fi/handle/10024/146461",
          },
          {
            value:
              "Kenen Tietoo? -Opas moninaisempaan ja inklusiivisempaan journalismiin.​​​​​",
            navigate: "https://raremedia.fi/kenentietoo/",
          },
          {
            value:
              "Moniäänistämisen haasteista, Jouko Jokisen ja Maria Pettersonin haastattelu​​​​​",
            navigate: "https://tutka.pro/yhteiskunta/jokaisella-journalistilla-on-moraalinen-vastuu-miettia-miten-minun-juttuni-muuttavat-maailmaa/",
          },
          {
            value:
              "Journalistien diversiteetistä​​​​​",
            navigate: "https://www.sttinfo.fi/tiedote/journalismi-tarvitsee-lisaa-diversiteettia?publisherId=69817828&releaseId=69919306",
          },
        ]}
        next={"navigate"}
      />
    </div>
  );
};
