import { QuestionNavbar } from "../QuestionNavbar.js";
import { Info } from "./components/Info.js";

export const Info6 = () => {
  return (
    <div className="infos">
      <QuestionNavbar />
      <Info
        title={[
          "Inklusiivisessa mediassa on kyse mahdollisuuksien luomisesta yhdenvertaiselle, toisia kunnioittavalle ja vastuulliselle mediaan osallistumiselle.",
          "Mediajulkisuuden inklusiivisuudella on tärkeä merkitys yhteiskunnallisen tasa-arvoisuuden, luottamuksen ja yhtenäisyyden säilyttämisessä. Jos media välittää vain virallista tai enemmistön kantaa, mahdollisesti osa kansalaisista kokee ulkopuolisuutta ja syrjäytymistä yhteiskunnasta.​​",
          "Mitä mahdollisuuksia sinulla on tuoda esiin valtavirrasta mahdollisesti eroavia näkemyksiä ja rakentaa inklusiivista mediajulkisuutta? Sietääkö toimitustyö epävarmuutta ja erilaisten näkemysten esittämistä inklusiivisuuden hengessä? Voivatko ihmiset kohdata toisensa tasa-arvoisesti ja ilmaista näkemyksensä turvallisesti mediajulkisuudessa? ​​",
        ]}
        options={[
          {
            value:
              "Alexandra Bordchardt (2022). Newsrooms that care: How diversity and inclusion will define the future of journalism. European Federation of Journalists (EFJ)​​​​",
            navigate: "https://europeanjournalists.org/blog/2022/04/14/new-report-calls-for-more-diversity-in-talents-in-journalism/",
          },
          {
            value:
              "Minna Horowitz et al (2023). Nuoret on monikko: Nuorten mediasuhteet ja mediavälitteinen tieto digitaalisten alustojen ajalla. Katsaus nuorten suhteista ja suhtautumisesta mediaan. ",
            navigate: "https://uploads-ssl.webflow.com/64123799970e0ddca93c317a/646c621918b7a9d90e6c5956_Nuoret%20ja%20media%20-raportti%20FINAL.pdf",
          },
        ]}
        next={"study/7"}
      />
    </div>
  );
};
