import { QuestionNavbar } from "../QuestionNavbar.js";
import { Info } from "./components/Info.js";

export const Info22 = () => {
  return (
    <div className="infos">
      <QuestionNavbar />
      <Info
        title={[
          "Viranomaisten auktorisoimat tutkimuslaitokset ja instituutiot ovat monesti ensimmäinen ja selkein tarttumakohta lähteisiin. Usein media suosii vakiintuneita asiantuntijalähteitä ja viranomaislausuntoja.​",
          "Tutkimuksemme mukaan kerronnan keinojen tulee olla vastuullisia. Haastatteluaineiston perusteella kerronnan tulisi olla moninäkökulmaista ja dialogista – ei vastakkain asettelevaa. Kerronnassa tulisi välttää ihmisten leimaamista, loukkaavia termejä ja syrjäyttäviä ilmaisutapoja. Sen sijaan kerronnassa olisi hyväksyttävä ihmisten moniulotteisuus ja myös valtavirrasta poikkeavat näkemykset. (Mäkinen & Pienimäki.)​​",
          "Tunnistatko oman tulkintakehyksesi? Miten se vaikuttaa kerrontaasi? Voitko välttää mustavalkoisia asetelmia kerronnassasi?​",
          "Tunnistatko oman tulkintakehyksesi? Miten se vaikuttaa kerrontaasi? Voitko välttää mustavalkoisia asetelmia kerronnassasi?​",
        ]}
        options={[
          {
            value:
              "Lassila-Merisalo, M. (2008): Uteliaille toimittajille pettymystä ei näytetä. Kaunokirjallisen journalismin poetiikkaa aikakauslehtireportaasissa. Kirjallisuudentutkimuksen aikakauslehti Avain, 4(1/2008), 6-25.​​",
            navigate:
              "https://journal.fi/avain/article/view/74720",
          },
          {
            value: "Lassila-Merisalo, M. (25.4.2022): Yksilöiden selviytymiskertomukset ovat arveluttavaa journalismia.",
            navigate: "https://journalisti.fi/nakokulmat/2022/04/yksiloiden-selviytymiskertomukset-ovat-arveluttavaa-journalismia-kirjoittaa-dosentti-maria-lassila-merisalo/",
          },
          {
            value: "Tiedetoimittaja Jani Kaaro (2022): kommentoi dosentti Maria Lassila-Merisalon ja lehtori Lauri Haapasen pitämää työpajaa Journalismin etiikka -seminaarissa 25.-26.8.2022. Journalismi tarinoi ja siteeraa – kuinka käy todenmukaisuuden?",
            navigate: "https://www.tiedetoimittajat.fi/journalismi-tarinoi-ja-siteeraa-kuinka-kay-todenmukaisuuden/",
          },
        ]}
      />
    </div>
  );
};
